import { Component, OnInit } from '@angular/core';

interface CardOption {
  value: string;
  image: string;
}

@Component({
  selector: 'app-card-selection',
  templateUrl: './card-selection.component.html',
  styleUrls: ['./card-selection.component.scss'],
})
export class CardSelectionComponent {
  constructor() {}
  // public cardOptions: CardOption[] = [
  //   { value: 'front seat', image: '../../assets/images/front-seat.png' },
  //   { value: 'back seat', image: '../../assets/images/rear-seat.png' },
  // ];
  // public selectedCards: string[] = [];
  // public selectedItem: any = {};

  // ngOnInit() {
  //   console.log(this.selectedCards);
  // }

  // setItem(item: any) {
  //   this.selectedItem = item;
  // }

  // goToNextPage() {
  //   // this.selectedCards.push(item);
  // }
  selectedOption: string = "";
  selectedAnswer: string[] = [];

  goToNextPage() {
    this.selectedAnswer.push(this.selectedOption);
    // Navigate to the second page
  }
}
