<mat-card>
  <h2>Select one option:</h2>
  <mat-radio-group [(ngModel)]="selectedOption">
    <mat-card>
      <mat-radio-button value="option1">Option 1</mat-radio-button>
      <p>Description for option 1</p>
    </mat-card>
    <mat-card>
      <mat-radio-button value="option2">Option 2</mat-radio-button>
      <p>Description for option 2</p>
    </mat-card>
  </mat-radio-group>
  <button mat-raised-button color="primary" (click)="goToNextPage()">Next</button>
</mat-card>
