import { Component, Input, OnInit, SimpleChanges, ChangeDetectionStrategy } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, FormGroupDirective, NgForm, Validators } from '@angular/forms';
import { ErrorStateMatcher } from '@angular/material/core';

interface JsonFormValidators {
  min?: number;
  max?: number;
  required?: boolean;
}


interface JsonFormControls {
  name: string;
  description: string;
  value: string;
  type: string;
  validators: JsonFormValidators;
}

interface JsonFormTypes {
  name: string;
  values: JsonFormControls[];
}
export interface JsonFormData {
  types: JsonFormTypes[];
}




export class MyErrorStateMatcher implements ErrorStateMatcher {
  isErrorState(
    control: FormControl | null,
    form: FormGroupDirective | NgForm | null
  ): boolean {
    const isSubmitted = form && form.submitted;
    return !!(
      control &&
      control.invalid &&
      (control.dirty || control.touched || isSubmitted)
    );
  }
}


@Component({
  selector: 'app-input-form',
  templateUrl: './input-form.component.html',
  styleUrls: ['./input-form.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class InputFormComponent implements OnInit {
  @Input()
  jsonFormData!: JsonFormData;

  @Input() answers: any;

  public myForm: FormGroup = this.fb.group({});

  constructor(private fb: FormBuilder) {}
  ngOnInit() {
    this.createForm(this.jsonFormData.types);
  }

  matcher = new MyErrorStateMatcher();

  createForm(types: JsonFormTypes[]) {
    for (const type of types) {
      for (const control of type.values) {
        const validatorsToAdd = [];
        for (const [key, value] of Object.entries(control.validators)) {
          switch (key) {
            case 'min':
              validatorsToAdd.push(Validators.min(value));
              break;
            case 'max':
              validatorsToAdd.push(Validators.max(value));
              break;
            case 'required':
              if (value) {
                validatorsToAdd.push(Validators.required);
              }
              break;
            case 'requiredTrue':
              if (value) {
                validatorsToAdd.push(Validators.requiredTrue);
              }
              break;
            case 'email':
              if (value) {
                validatorsToAdd.push(Validators.email);
              }
              break;
            case 'minLength':
              validatorsToAdd.push(Validators.minLength(value));
              break;
            case 'maxLength':
              validatorsToAdd.push(Validators.maxLength(value));
              break;
            case 'pattern':
              validatorsToAdd.push(Validators.pattern(value));
              break;
            case 'nullValidator':
              if (value) {
                validatorsToAdd.push(Validators.nullValidator);
              }
              break;
            default:
              break;
          }

          this.myForm.addControl(
            control.name,
            this.fb.control(control.value, validatorsToAdd)
          );
        }
      }
    }
  }

  onSubmit() {
    console.log('Form valid: ', this.myForm.valid);
    console.log('Form values: ', this.myForm.value);
  }
}


<mat-card class="question-form-container">
  <mat-card-content>
    <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label *ngFor="let type of jsonFormData?.types">
          <label *ngFor="let control of type.values">
            <li>
              <span class="choice-container">
                <label class="name">{{ control.name }}</label>
                <label class="description">{{ control.description }}</label>
                <input [type]="control.type" [formControlName]="control.name" [value]="control.value" matInput>
              </span>

            </li>
          </label>
          <button type="submit">Submit</button>
        </label>
      </div>
    </form>

  </mat-card-content>


</mat-card>
