import { Component, Input, OnChanges, SimpleChanges, ChangeDetectionStrategy } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


interface JsonFormValidators {
  min?: number;
  max?: number;
  required?: boolean;
}


interface JsonFormControls {
  name: string;
  description: string;
  value: string;
  type: string;
  validators: JsonFormValidators;
}

interface JsonFormTypes {
  name: string;
  values: JsonFormControls[];
}
export interface JsonFormData {
  types: JsonFormTypes[];
}


@Component({
  selector: 'app-input-form',
  templateUrl: './input-form.component.html',
  styleUrls: ['./input-form.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class InputFormComponent implements OnChanges {
  @Input()
  jsonFormData!: JsonFormData;

  @Input() answers: any;

  public myForm: FormGroup = this.fb.group({});

  constructor(private fb: FormBuilder) {}
  ngOnChanges(changes: SimpleChanges) {
    this.createForm(this.jsonFormData.types);
  }

  createForm(types: JsonFormTypes[]) {
    for (const type of types) {
      for (const control of type.values) {
        const validatorsToAdd = [];
        for (const [key, value] of Object.entries(control.validators)) {
          switch (key) {
            case 'min':
              validatorsToAdd.push(Validators.min(value));
              break;
            case 'max':
              validatorsToAdd.push(Validators.max(value));
              break;
            case 'required':
              if (value) {
                validatorsToAdd.push(Validators.required);
              }
              break;
            default:
              break;
          }
        }

        this.myForm.addControl(
          control.name,
          this.fb.control(control.value, validatorsToAdd)
        );
      }
    }
  }

  onSubmit() {
    console.log('Form valid: ', this.myForm.valid);
    console.log('Form values: ', this.myForm.value);
  }
}


<mat-card class="question-form-container">
  <mat-card-content>
    <form [formGroup]="myForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label *ngFor="let type of jsonFormData?.types">
          <label *ngFor="let control of type.values">
            <li>
              <span class="choice-container">
                <label class="name">{{ control.name }}</label>
                <img src="{{c}}" alt="">
                <div>
                  <input [type]="control.type" [formControlName]="control.name" [value]="control.value" required>
                  <div *ngIf="myForm.controls[control.name].hasError('max')" class="alert alert-danger">
                    Max required number is <strong>{{control.validators.max}}MM</strong>.
                  </div>
                  <div *ngIf="myForm.controls[control.name].hasError('min')" class="alert alert-danger">
                    Min required number is <strong>{{control.validators.min}}MM</strong>.
                  </div>
                </div>
              </span>
            </li>
          </label>
          <button mat-raised-button class="btn btn-success" color="primary" type="submit">Submit</button>
        </label>
      </div>
    </form>

  </mat-card-content>


</mat-card>
