import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-card-selection',
  templateUrl: './card-selection.component.html',
  styleUrls: ['./card-selection.component.scss']
})

interface CardOption {
  value: string;
  label: string;
}

export class CardSelectionComponent {

public cardOptions: CardOption[] = [
  { value: 'card1', label: 'Card 1' },
  { value: 'card2', label: 'Card 2' },
  { value: 'card3', label: 'Card 3' },
  { value: 'card4', label: 'Card 4' },
  { value: 'card5', label: 'Card 5' }
];

public selectedCards: string[] = [];

toggleSelected(cardOption: CardOption) {
  const index = this.selectedCards.indexOf(cardOption.value);
  if (index >= 0) {
    this.selectedCards.splice(index, 1);
  } else {
    this.selectedCards.push(cardOption.value);
  }
}
}
}
